import jxl.*
def filepath ="C://Users//Administrator//Desktop//custome rrepo.xls"
File Excel = new File(filepath)
def email
def firstname
def lastname
Workbook wb = Workbook.getWorkbook(Excel)
Sheet sh = wb.getSheet("Sheet1")
def row = sh.getRows().toInteger()
def column = sh.getColumns().toInteger()
log.info " $row and The Number of column are $column"
for(def i=0;i<row;i++)
{
	for(def j=0;j<column;j++)
	{
		if(j==0)
		{
			email = sh.getCell(j,i).getContents()
		}
		else if(j==1)
		{
			firstname = sh.getCell(j,i).getContents()
		}
		else
		{    lastname = sh.getCell(j,i).getContents()
		}
	}
log.info " $email and $firstname and $lastname"
}
def tc = testRunner.testCase.testSuite.project.testSuites["testing"].testCases["property expansion"]
tc.testSteps["email,name"].setPropertyValue("email",email)
tc.testSteps["email,name"].setPropertyValue("name",firstname)
tc.testSteps["email,name"].setPropertyValue("lastname",lastname)
def runner = tc.run(null,false)
def teststeplist = tc.getTestStepList()
teststeplist.each
{
	def tsname = it.name
	log.info tsname
}
log.info "Status: $runner.status "
log.info "TimeTaken: $runner.timeTaken"
log.info "Number of Steps executed is :" + runner.results.size()
assert runner.results.size()==4
